	RealType function ddbar_sumup(CCloop, CCtree)
	implicit none

#include "ddbar_vars.h"

	ComplexType CCloop(1), CCtree(1)
	ComplexType m

	ddbar_sumup = 0

	m = 0
	m = m + CCloop(1)*MatSUN(1,1)
	ddbar_sumup = ddbar_sumup + Re(Conjugate(CCtree(1))*m)
	end

