	subroutine ddbar_SquaredMEHel(result, flags)
	implicit none
	RealType result(*)
	integer flags

#include "ddbar_vars.h"

	RealType ddbar_sumup
	external ddbar_sumup

* BEGIN ABBR_HEL
	call ddbar_abbr0hel
	TEST(flags, BIT_LOOP)
	call ddbar_abbr1hel
	ENDTEST(flags, BIT_LOOP)
* END ABBR_HEL

* BEGIN FF_INI
	Ctree(1) = 0

	Cloop(1) = 0
* END FF_INI

* BEGIN FF_TREE
	call ddbar_born

	result(1) = result(1) + ddbar_sumup(Ctree, Ctree)
* END FF_TREE

	TEST(flags, BIT_LOOP)
* BEGIN FF_LOOP
	call ddbar_self
	call ddbar_vert
	call ddbar_box

	result(2) = result(2) + 2*ddbar_sumup(Cloop, Ctree)
* END FF_LOOP
	ENDTEST(flags, BIT_LOOP)
	end

